sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate) {
		var today = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		today = today.getLocalDate();
		testdate = testdate.getLocalDate();

		if(testdate.before(today)) return true;

		return false;

	},
	isToday: function(strDate) {
		var today = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		today = today.getLocalDate();
		testdate = testdate.getLocalDate();

		if(testdate.compareTo(today) == 0) return true;

		return false;
	},
    type: 'NeedItUtils'
};
